// Parse and format Lemlist conversation history for AI agent

// Helper function to strip HTML tags and decode HTML entities
function stripHtml(html) {
  if (!html) return '';
  let text = html.replace(/<[^>]*>/g, ' ');
  text = text
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/&#x27;/g, "'")
    .replace(/&apos;/g, "'")
    .replace(/&#39;s/g, "'s");
  text = text.replace(/\s+/g, ' ').trim();
  return text;
}

// Helper function to format timestamp
function formatTimestamp(isoDate) {
  const date = new Date(isoDate);
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  const hours = String(date.getHours()).padStart(2, '0');
  const minutes = String(date.getMinutes()).padStart(2, '0');
  return `${year}-${month}-${day} ${hours}:${minutes}`;
}

// Get the conversation data from the input
const inputData = $input.all();
let rawMessages = [];

if (Array.isArray(inputData) && inputData.length > 0) {
  const firstItem = inputData[0].json;
  if (firstItem?.data && Array.isArray(firstItem.data)) {
    rawMessages = firstItem.data;
  } else if (Array.isArray(firstItem)) {
    rawMessages = firstItem;
  }
}

// 1. EXTRACT LEAD-SPECIFIC DATA
const latestLeadReply = rawMessages.find(msg => msg.type === 'emailsReplied');
const anyMessageWithEmail = rawMessages.find(msg => msg.leadEmail);

let leadMessage = '';
let leadEmail = anyMessageWithEmail ? anyMessageWithEmail.leadEmail : null;
let companyName = null;

if (latestLeadReply && latestLeadReply.message) {
    let cleaned = stripHtml(latestLeadReply.message);
    leadMessage = cleaned.split(/On\s.*wrote:/i)[0]
                         .split(/From:/i)[0]
                         .split(/---/)[0]
                         .trim();
                         // Removed .substring(0, 1000) to keep full text
}

if (leadEmail && leadEmail.includes('@')) {
  const domain = leadEmail.split('@')[1].split('.')[0];
  companyName = domain.charAt(0).toUpperCase() + domain.slice(1);
}

// 2. PROCESS ALL MESSAGES FOR THE THREAD
const processedMessages = rawMessages.map(item => {
  const isProspect = item.type === 'emailsReplied';
  const role = isProspect ? 'Prospect' : 'SDR';
  const senderName = isProspect 
    ? (item.fromEmail ? item.fromEmail.split('@')[0] : 'Prospect') 
    : (item.sendUserName || 'SDR');

  return {
    timestamp: item.createdAt,
    formattedTime: formatTimestamp(item.createdAt),
    role: role,
    sender: senderName,
    // MODIFIED: Removed .substring(0, 500) to show the full message
    message: stripHtml(item.message),
    type: item.type,
    subject: item.subject || ''
  };
});

// Sort messages Newest First
const chronologicalMessages = [...processedMessages].sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));

// 3. FORMAT CONVERSATION THREAD
const conversationThread = chronologicalMessages.map((msg, index) => {
  const isLatest = index === 0;
  const prefix = isLatest ? '[LATEST] ' : '';
  return `${prefix}[${msg.formattedTime}] ${msg.role} (${msg.sender}): ${msg.message}`;
}).join('\n\n');

// 4. CREATE STRUCTURED OBJECT
const structuredConversation = {
  totalMessages: chronologicalMessages.length,
  messages: chronologicalMessages,
  summary: {
    prospectMessageCount: chronologicalMessages.filter(m => m.role === 'Prospect').length,
    sdrMessageCount: chronologicalMessages.filter(m => m.role === 'SDR').length,
    latestRole: chronologicalMessages[0]?.role || 'Unknown'
  }
};

return {
  json: {
    leadMessage: leadMessage,
    leadEmail: leadEmail,
    companyName: companyName,
    parsedConversation: structuredConversation,
    conversationThreadFormatted: conversationThread,
    contactId: rawMessages[0]?.contactId || null,
    subject: rawMessages[0]?.subject || ''
  }
};

