// Prepare training document for Vector Store
const leadMessage = $('Parse Conversation History').first().json.leadMessage;
const responseSent = $('Parse Slack Payload').item.json.ai_generated_response;
const topic = $json.output?.topic || $json.topic || 'general_inquiry';

const now = new Date();
const timestamp = now.toLocaleString('en-SG', {
  timeZone: 'Asia/Singapore',
  year: 'numeric',
  month: 'long',
  day: 'numeric',
  hour: '2-digit',
  minute: '2-digit',
  hour12: true
});

const cleanedResponse = responseSent
  .replace(/\\n/g, ' ')
  .replace(/\s+/g, ' ')
  .trim();

// Return as a single item
return {
  json: {
    pageContent: leadMessage, //Standard key for text
    custom_metadata: {
      topic: topic,
      response_sent: cleanedResponse,
      created_at: timestamp
    }
  }
};

