// Parse conversation history to extract subject, metadata, and lead message

  const messages = $input.first().json.data || [];
  const latestMessage = messages[0] || {};

  // Extract subject line
  let subject = latestMessage.subject || 'Re: Follow-up';
  if (!subject.toLowerCase().startsWith('re:')) {
    subject = 'Re: ' + subject;
  }

  // Find the first email sent by us for metadata
  const sentMessage = messages.find(msg => msg.type === 'emailsSent') || latestMessage;

  // Find the latest lead message (type: "emailsReplied")
  const leadReply = messages.find(msg => msg.type === 'emailsReplied');
  let leadMessage = '';

  if (leadReply && leadReply.message) {
    // Clean up HTML tags and decode entities
    leadMessage = leadReply.message
      .replace(/<[^>]*>/g, '')           // Remove HTML tags
      .replace(/&lt;/g, '<')             // Decode HTML entities
      .replace(/&gt;/g, '>')
      .replace(/&amp;/g, '&')
      .replace(/&nbsp;/g, ' ')
      .replace(/&#39;/g, "'")            // Decode apostrophe
      .replace(/&quot;/g, '"')           // Decode quotes
      .replace(/&#x27;/g, "'")           // Decode hex apostrophe
      .replace(/&apos;/g, "'")           // Decode apos
      .replace(/\s+/g, ' ')              // Collapse multiple spaces
      .trim()                            // Remove leading/trailing whitespace
      .substring(0, 500);                // Truncate to 500 characters
  }

  // Handle CC
  let cc = null;
  if (Array.isArray(sentMessage.cc)) {
    cc = sentMessage.cc.length > 0 ? sentMessage.cc : null;
  } else if (Array.isArray(latestMessage.cc)) {
    cc = latestMessage.cc.length > 0 ? latestMessage.cc : null;
  }

  return {
    json: {
      subject: subject,
      sendUserId: sentMessage.sendUserId || null,
      sendUserEmail: sentMessage.sendUserEmail || null,
      sendUserMailboxId: sentMessage.sendUserMailboxId || null,
      contactId: latestMessage.contactId || null,
      leadId: latestMessage.leadId || null,
      cc: cc,
      leadMessage: leadMessage  // Cleaned and truncated lead message
    }
  };

// Parse Slack interaction payload for RLHF

const payload = $input.first().json.body.payload;
let parsedPayload;

if (typeof payload === 'string') {
  parsedPayload = JSON.parse(payload);
} else {
  parsedPayload = payload;
}

// Extract basic action details
const action = parsedPayload.actions?.[0];
const actionId = action?.action_id;
const user = parsedPayload.user;
const channel = parsedPayload.channel;
const responseUrl = parsedPayload.response_url;

// Helper function to extract text from markdown field
function extractFromField(blocks, label) {
  for (const block of blocks) {
    if (block.type === 'section' && block.fields) {
      for (const field of block.fields) {
        if (field.text && field.text.includes(label)) {
          const parts = field.text.split('\n');
          if (parts.length > 1) {
            let value = parts[1];
            const emailMatch = value.match(/<mailto:([^|>]+)\|/);
            if (emailMatch) {
              return emailMatch[1];
            }
            return value.trim();
          }
        }
      }
    }
  }
  return null;
}

// Helper function to extract text from section with specific header
function extractSectionText(blocks, header) {
  for (const block of blocks) {
    if (block.type === 'section' && block.text && block.text.text) {
      if (block.text.text.includes(header)) {
        const text = block.text.text;
        const codeBlockMatch = text.match(/```([\s\S]*?)```/);
        if (codeBlockMatch) {
          return codeBlockMatch[1].trim();
        }
        const parts = text.split(header);
        if (parts.length > 1) {
          return parts[1].replace(/^\n/, '').trim();
        }
      }
    }
  }
  return null;
}

// Helper function to extract Lemlist IDs from URL in context block
function extractLemlistIds(blocks) {
  for (const block of blocks) {
    if (block.type === 'context' && block.elements) {
      for (const element of block.elements) {
        if (element.text && element.text.includes('app.lemlist.com')) {
          const urlMatch = element.text.match(/https:\/\/app\.lemlist\.com\/teams\/([^\/]+)\/inbox\/list\/myConversations\/contacts\/([^|>]+)/);
          if (urlMatch) {
            return {
              teamId: urlMatch[1],
              contactId: urlMatch[2]
            };
          }
        }
      }
    }
  }
  return { teamId: null, contactId: null };
}

const blocks = parsedPayload.message?.blocks || [];

const companyName = extractFromField(blocks, '*Company:*');
const leadEmail = extractFromField(blocks, '*Lead Email:*');
const replyFromEmail = extractFromField(blocks, '*Reply-from Email:*');
const outreachCampaign = extractFromField(blocks, '*Outreach Campaign:*');
const prospectReply = extractSectionText(blocks, "*Prospect's Reply:*");
const agentResponse = extractSectionText(blocks, '*Agent Generated Response:*');
const lemlistIds = extractLemlistIds(blocks);
const lemlistMessageId = action?.value;

return {
  json: {
    action_id: actionId,
    slack_channel_id: channel?.id,
    slack_channel_name: channel?.name,
    slack_user_name: user?.name || user?.username,
    company_name: companyName,
    lead_identifier: leadEmail,
    reply_from_email: replyFromEmail,
    campaign_name: outreachCampaign,
    lead_message: prospectReply,
    ai_generated_response: agentResponse,
    lemlist_contact_id: lemlistIds.contactId,
    lemlist_team_id: lemlistIds.teamId,
    lemlist_message_id: lemlistMessageId,
    user_id: user?.id,
    response_url: responseUrl,
    original_message_ts: parsedPayload.message?.ts,
    team_id: parsedPayload.team?.id
  }
};

