// Loop through all input items
return items.map(item => {
    // Pull OpenAI text
    const openAIText = item.json.analysis[0].candidates[0].content.parts[0].text;

    // Parse the string into JSON
    const parsed = JSON.parse(openAIText);

    // Attach parsed fields to json for easier use downstream
    item.json = {
        ...item.json,
        "Candidate Name": parsed.candidate_name,
        "Email": parsed.email,
        "Age": parsed.age,
        "Highest Education Level": parsed.highest_education_level,
        "Years of Experience": parsed.total_years_experience,
        "Relevant Years of Experience": parsed.relevant_years_experience,
        "Coachability Score": parsed.traits.coachability.score,
        "Coachability Evidence": parsed.traits.coachability.evidence,
        "Curiosity Score": parsed.traits.curiosity.score,
        "Curiosity Evidence": parsed.traits.curiosity.evidence,
        "Prior Success Score": parsed.traits.prior_success.score,
        "Prior Success Evidence": parsed.traits.prior_success.evidence,
        "Intelligence Score": parsed.traits.intelligence.score,
        "Intelligence Evidence": parsed.traits.intelligence.evidence,
        "Work Ethic Score": parsed.traits.work_ethic.score,
        "Work Ethic Evidence": parsed.traits.work_ethic.evidence,
        "Total Score": parsed.total_score,
        "Strengths": parsed.strengths,
        "Potential Concerns": parsed.potential_concerns,
        "Summary": parsed.recruiter_summary,
        "Resume Link": parsed.resume_url,
        candidate_data: parsed // keep original object if needed
    };

    return item;
});

// Loop through all items and copy the existing binary file
return items.map(item => {
    if(item.binary && item.binary.data){
        // Duplicate the binary property to a new name
        item.binary.resume_file = item.binary.data;
    }
    return item;
});
