const items = $input.all();
const output = [];

items.forEach(item => {
  const messages = item.json.results || [];
  if (messages.length === 0) return;

  // 1. Sort messages by createdAt (Oldest to Newest)
  const sortedMessages = [...messages].sort((a, b) => 
    new Date(a.createdAt) - new Date(b.createdAt)
  );

  let conversationThread = [];
  let latestLeadMessage = "";
  let lastProspectTime = null;
  let firstSdrReplyTime = null;

  sortedMessages.forEach(msg => {
    const isProspect = msg.sender.type === 'contact';
    const roleLabel = isProspect ? 'Prospect' : 'SDR';

    // Standardize on createdAt
    const msgDate = new Date(msg.createdAt);
    const dateStr = msgDate.toISOString().replace('T', ' ').substring(0, 16);
    const body = msg.body?.text?.text || "[Media/Other]";

    // Build Thread
    conversationThread.push(`[${dateStr}] ${roleLabel}: ${body}`);

    if (isProspect) {
      latestLeadMessage = body;
      lastProspectTime = msgDate;
      // Reset reply tracker so we find the SDR message that follows THIS prospect message
      firstSdrReplyTime = null;
    } else if (msg.sender.type === 'user' && lastProspectTime && !firstSdrReplyTime) {
      // Capture the first SDR reply that occurs after the most recent Prospect message
      firstSdrReplyTime = msgDate;
    }
  });

  // 2. Calculate Response Time (Minutes)
  let responseTimeMinutes = 0;
  if (lastProspectTime && firstSdrReplyTime) {
    const diffMs = firstSdrReplyTime - lastProspectTime;
    responseTimeMinutes = Math.round(diffMs / 60000);
  }

  output.push({
    leadMessage: latestLeadMessage,
    conversationId: sortedMessages[0].conversationId,
    conversationThreadFormatted: conversationThread.join('\n\n'),
    responseTimeMinutes: responseTimeMinutes,
    summary: {
      totalMessages: sortedMessages.length,
      latestRole: sortedMessages[sortedMessages.length - 1].sender.type === 'contact' ? 'Prospect' : 'SDR'
    }
  });
});

return output;

// This node extracts image URLs from the Whapi payload
const messages = $input.item.json.body.messages;
const imagesFound = [];

if (messages && Array.isArray(messages)) {
  for (const msg of messages) {
    // Check if the message type is image and has a link
    if (msg.type === 'image' && msg.image && msg.image.link) {
      imagesFound.push({
        imageUrl: msg.image.link,
        caption: msg.image.caption || "",
        chatId: msg.chat_id,
        sender: msg.from_name
      });
    }
  }
}

// If no images were found, you might want to return an empty item 
// or a specific message to avoid errors in later nodes
return imagesFound.length > 0 ? imagesFound : [{ imageUrl: null, status: "no image found" }];

// This node extracts voice/audio URLs from the Whapi payload
const messages = $input.item.json.body.messages;
const audioFound = [];

if (messages && Array.isArray(messages)) {
  for (const msg of messages) {
    // Whapi uses 'voice' for voice notes and 'audio' for uploaded files
    const audioData = msg.voice || msg.audio;

    if ((msg.type === 'voice' || msg.type === 'audio') && audioData && audioData.link) {
      audioFound.push({
        audioUrl: audioData.link,
        chatId: msg.chat_id,
        sender: msg.from_name,
        seconds: audioData.seconds || 0,
        mime_type: audioData.mime_type
      });
    }
  }
}

// If no audio was found, return a null object to prevent workflow crashes
return audioFound.length > 0 ? audioFound : [{ audioUrl: null, status: "no audio found" }];

// This node extracts video URLs from the Whapi payload
const messages = $input.item.json.body.messages;
const videosFound = [];

if (messages && Array.isArray(messages)) {
  for (const msg of messages) {
    // Check if the message type is video and has a link
    if (msg.type === 'video' && msg.video && msg.video.link) {
      videosFound.push({
        videoUrl: msg.video.link,
        caption: msg.video.caption || "",
        seconds: msg.video.seconds || 0,
        chatId: msg.chat_id,
        sender: msg.from_name
      });
    }
  }
}

// Return video data or a null object to keep the workflow from breaking
return videosFound.length > 0 ? videosFound : [{ videoUrl: null, status: "no video found" }];

